import './App.css';
import AboutMe from './pages/aboutme';
import Projects from './pages/projects';
import { useState, useEffect } from 'react';

function renderPage(currentPage) {
  if (currentPage === 'AboutMe') {

    return <AboutMe />
  }
  if (currentPage === 'Projects') {

    return <Projects />
  }
}


function App() {
  const [currentPage, setCurrentPage] = useState('AboutMe');

  return (
    <div>
      <nav>
      <h1 className='name'>Nathan Hawk</h1>
      <a href='aboutme' onClick={() => setCurrentPage('AboutMe')} className='about-me nav-btn hvr-fade'>About Me</a>
      <a href='myprojects' onClick={() => setCurrentPage('Projects')} className='projects nav-btn hvr-fade'>My Projects</a>
      <a href='myprojects' onClick={() => useEffect(() => {setCurrentPage('Projects')})} className='projects nav-btn hvr-fade'>My Projects</a>

    </nav>
      {renderPage(currentPage)}
    </div>
  );
}

export default App;
